import React,{useState} from 'react';
import Head from './components/Head'
import List from './components/List'
import Foot from './components/Foot'

function App() {
  const [count, setCount] = useState(0);
  const state = {
    todos: [
      { id: 1, title: '看一小时React的课程', finished: false },
      { id: 2, title: '打一小时篮球', finished: false },
      { id: 3, title: '游泳一小时', finished: false },
    ],
    finishedCount: 0
  }


   // 1. 修改完成状态
   const changeTodoFinished = (todoId, isFinished) => {
    // 遍历
    const tempTodos = state.todos;
    let finishedCount = 0;
    tempTodos.forEach((todo) => {
        if (todo.id === todoId) {
            todo.finished = isFinished;
        }
        if (todo.finished) {
            finishedCount += 1;
        }
    });
    //  更新状态
    state.todos = tempTodos;
    state.finishedCount = finishedCount
    console.log("state.todos",state.todos)
    console.log("state.finishedCount",state.finishedCount)
};
  return (
    <div className="todo-container" >
      <div className="todo-wrap">
        <Head />
        <List todos={state.todos} changeTodoFinished={changeTodoFinished}/>
        <Foot />
      </div>
    </div>
  );
}

export default App;
